/**
 * https://leetcode.com/problems/add-strings/description/
 *
 * Output:
 * ------
 * 134
 * 533
 * 0
 * 
 */

public class AddStrings {

    public String addStrings(String num1, String num2) {
        String low = num1;
        int lowLen = num1.length();
        String high = num2;
        int highLen = num2.length();

        if (highLen < lowLen) {
            lowLen = num2.length();
            low = num2;
            highLen = num1.length();
            high = num1;
        }
        
        int carry = 0;
        String sum = "";

        for (int i = 0; i < lowLen; i++) {
            int val = (low.charAt(lowLen-i-1)-'0') + (high.charAt(highLen-i-1)-'0') + carry;
            carry = val/10;
            sum = String.valueOf(val%10) + sum;
        }
        int balance = highLen - lowLen;
        if (balance == 0 && carry > 0) {
            sum = String.valueOf(carry) + sum;
        } else if (balance == 0 && carry == 0) {
        } else {
            for (int i = 0; i < balance; i++) {
                int val = (carry > 0)? carry + (high.charAt(balance-i-1)-'0'):
                                        high.charAt(balance-i-1)-'0';
                carry = val/10;
                sum = String.valueOf(val%10) + sum;
            }
            if (carry > 0) {
            	sum = String.valueOf(carry) + sum;
            }
        }
        return sum;
    }

    public static void main(String[] args) {
        System.out.println(new AddStrings().addStrings("11", "123"));
        System.out.println(new AddStrings().addStrings("456", "77"));
        System.out.println(new AddStrings().addStrings("0", "0"));
        System.out.println(new AddStrings().addStrings("", ""));
    }

}

